import React, { useEffect, useState } from 'react';
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  HStack,
  Text,
  useToast,
  Container,
  StackDivider,
  IconButton,
  useColorMode,
  Flex,
  Spacer,
  useBreakpointValue
} from '@chakra-ui/react';
import { DeleteIcon, EditIcon, MoonIcon, SunIcon } from '@chakra-ui/icons';
import {
  getReminders,
  addReminder,
  deleteReminder,
  updateReminder
} from './services/api';

function App() {
  const [form, setForm] = useState({
    medicineName: '',
    dosageTime: '',
    frequency: ''
  });

  const [reminders, setReminders] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const toast = useToast();
  const { colorMode, toggleColorMode } = useColorMode();
  const isMobile = useBreakpointValue({ base: true, md: false });

  useEffect(() => {
    fetchReminders();
  }, []);

  const fetchReminders = async () => {
    const res = await getReminders();
    setReminders(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.medicineName || !form.dosageTime || !form.frequency) return;

    if (editingId) {
      await updateReminder(editingId, form);
      toast({ title: 'Reminder updated.', status: 'success', duration: 2000, isClosable: true });
    } else {
      await addReminder(form);
      toast({ title: 'Reminder added.', status: 'success', duration: 2000, isClosable: true });
    }

    setForm({ medicineName: '', dosageTime: '', frequency: '' });
    setEditingId(null);
    fetchReminders();
  };

  const handleDelete = async (id) => {
    await deleteReminder(id);
    toast({ title: 'Reminder deleted.', status: 'info', duration: 2000, isClosable: true });
    fetchReminders();
  };

  const handleEdit = (reminder) => {
    setForm({
      medicineName: reminder.medicineName,
      dosageTime: reminder.dosageTime,
      frequency: reminder.frequency
    });
    setEditingId(reminder.id);
  };

  return (
    <Container maxW={{ base: '95%', md: 'lg' }} py={10}>
      <Flex align="center" justify="space-between" mb={6}>
        <Heading fontSize={{ base: 'xl', md: '2xl' }} color="teal.500">
          üíä Medicine Reminder
        </Heading>
        <IconButton
          icon={colorMode === 'light' ? <MoonIcon /> : <SunIcon />}
          onClick={toggleColorMode}
          isRound
          aria-label="Toggle color mode"
        />
      </Flex>

      <VStack
        as="form"
        spacing={4}
        onSubmit={handleSubmit}
        p={6}
        borderWidth={1}
        borderRadius="lg"
        boxShadow="md"
        bg={colorMode === 'light' ? 'white' : 'gray.800'}
      >
        <Input
          placeholder="Medicine Name"
          value={form.medicineName}
          onChange={(e) => setForm({ ...form, medicineName: e.target.value })}
        />
        <Input
          placeholder="Dosage Time (e.g. 8:00 AM)"
          value={form.dosageTime}
          onChange={(e) => setForm({ ...form, dosageTime: e.target.value })}
        />
        <Input
          placeholder="Frequency (e.g. Daily)"
          value={form.frequency}
          onChange={(e) => setForm({ ...form, frequency: e.target.value })}
        />
        <Button
          colorScheme={editingId ? 'yellow' : 'teal'}
          type="submit"
          width="100%"
          size="lg"
          fontWeight="bold"
        >
          {editingId ? '‚úèÔ∏è Update Reminder' : '‚ûï Add Reminder'}
        </Button>
      </VStack>

      <Box mt={10}>
        <Heading as="h2" size="md" mb={4} color={colorMode === 'light' ? 'gray.700' : 'gray.200'}>
          Your Reminders
        </Heading>
        <VStack
          divider={<StackDivider borderColor={colorMode === 'light' ? 'gray.200' : 'gray.600'} />}
          spacing={4}
          align="stretch"
          borderWidth={1}
          borderRadius="lg"
          p={4}
          bg={colorMode === 'light' ? 'gray.50' : 'gray.700'}
          boxShadow="sm"
        >
          {reminders.length === 0 ? (
            <Text color="gray.500" textAlign="center">
              No reminders yet.
            </Text>
          ) : (
            reminders.map((reminder) => (
              <HStack key={reminder.id} justify="space-between" align="center">
                <Box>
                  <Text fontWeight="semibold" fontSize={{ base: 'md', md: 'lg' }}>
                    {reminder.medicineName}
                  </Text>
                  <Text fontSize="sm" color="gray.500">
                    {reminder.dosageTime} ‚Äî {reminder.frequency}
                  </Text>
                </Box>
                <HStack spacing={2}>
                  <IconButton
                    icon={<EditIcon />}
                    colorScheme="yellow"
                    size="sm"
                    onClick={() => handleEdit(reminder)}
                    aria-label="Edit reminder"
                  />
                  <IconButton
                    icon={<DeleteIcon />}
                    colorScheme="red"
                    size="sm"
                    onClick={() => handleDelete(reminder.id)}
                    aria-label="Delete reminder"
                  />
                </HStack>
              </HStack>
            ))
          )}
        </VStack>
      </Box>
    </Container>
  );
}

export default App;
