import React, { useEffect, useState } from 'react';
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  HStack,
  Text,
  useToast,
  Container,
  IconButton,
  useColorMode,
  Stack,
  Divider,
  Flex,
  useBreakpointValue,
  useColorModeValue
} from '@chakra-ui/react';
import { MoonIcon, SunIcon, DeleteIcon, EditIcon } from '@chakra-ui/icons';
import {
  getReminders,
  addReminder,
  deleteReminder,
  updateReminder
} from './services/api';

function App() {
  const [form, setForm] = useState({
    medicineName: '',
    dosageTime: '',
    frequency: ''
  });
  const [reminders, setReminders] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const toast = useToast();
  const { colorMode, toggleColorMode } = useColorMode();
  const isMobile = useBreakpointValue({ base: true, md: false });
  const cardBg = useColorModeValue('gray.50', 'gray.800');
  const formBg = useColorModeValue('white', 'gray.700');

  useEffect(() => {
    fetchReminders();
  }, []);

  const fetchReminders = async () => {
    try {
      const res = await getReminders();
      setReminders(res.data);
    } catch (error) {
      toast({ title: 'Failed to load reminders.', status: 'error', duration: 2000, isClosable: true });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { medicineName, dosageTime, frequency } = form;
    if (!medicineName || !dosageTime || !frequency) {
      toast({ title: 'All fields are required.', status: 'warning', duration: 2000, isClosable: true });
      return;
    }

    try {
      if (editingId) {
        await updateReminder(editingId, form);
        toast({ title: 'Reminder updated.', status: 'success', duration: 2000, isClosable: true });
      } else {
        await addReminder(form);
        toast({ title: 'Reminder added.', status: 'success', duration: 2000, isClosable: true });
      }
      setForm({ medicineName: '', dosageTime: '', frequency: '' });
      setEditingId(null);
      fetchReminders();
    } catch (error) {
      toast({ title: 'Network Error', description: 'Could not connect to backend.', status: 'error', duration: 2000, isClosable: true });
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteReminder(id);
      toast({ title: 'Reminder deleted.', status: 'info', duration: 2000, isClosable: true });
      fetchReminders();
    } catch (error) {
      toast({ title: 'Failed to delete reminder.', status: 'error', duration: 2000, isClosable: true });
    }
  };

  const handleEdit = (reminder) => {
    setForm({
      medicineName: reminder.medicineName,
      dosageTime: reminder.dosageTime,
      frequency: reminder.frequency
    });
    setEditingId(reminder.id);
  };

  return (
    <Container maxW="2xl" py={10} px={4}>
      <Flex mb={6} justify="space-between" align="center">
        <Heading size="lg" color="teal.500">
          üíä Medicine Reminder
        </Heading>
        <IconButton
          icon={colorMode === 'light' ? <MoonIcon /> : <SunIcon />} 
          onClick={toggleColorMode}
          aria-label="Toggle dark mode"
          isRound
        />
      </Flex>

      <VStack as="form" spacing={4} onSubmit={handleSubmit} mb={10} p={6} bg={formBg} rounded="lg" shadow="md">
        <Input
          placeholder="Medicine Name"
          value={form.medicineName}
          onChange={(e) => setForm({ ...form, medicineName: e.target.value })}
        />
        <Input
          placeholder="Dosage Time (e.g. 8:00 AM)"
          value={form.dosageTime}
          onChange={(e) => setForm({ ...form, dosageTime: e.target.value })}
        />
        <Input
          placeholder="Frequency (e.g. Daily)"
          value={form.frequency}
          onChange={(e) => setForm({ ...form, frequency: e.target.value })}
        />
        <Button colorScheme={editingId ? 'yellow' : 'teal'} type="submit" width="full">
          {editingId ? '‚úèÔ∏è Update Reminder' : '‚ûï Add Reminder'}
        </Button>
      </VStack>

      <Box>
        <Heading as="h2" size="md" mb={4}>
          Your Reminders
        </Heading>
        <Stack spacing={4} divider={<Divider />}> 
          {reminders.length === 0 ? (
            <Text textAlign="center" color="gray.500">
              No reminders yet.
            </Text>
          ) : (
            reminders.map((reminder) => (
              <Flex key={reminder.id} align="center" justify="space-between" p={4} rounded="lg" shadow="sm" bg={cardBg}>
                <Box>
                  <Text fontWeight="bold">{reminder.medicineName}</Text>
                  <Text fontSize="sm" color="gray.500">
                    {reminder.dosageTime} ‚Ä¢ {reminder.frequency}
                  </Text>
                </Box>
                <HStack spacing={2}>
                  <IconButton icon={<EditIcon />} onClick={() => handleEdit(reminder)} size="sm" colorScheme="yellow" aria-label="Edit" />
                  <IconButton icon={<DeleteIcon />} onClick={() => handleDelete(reminder.id)} size="sm" colorScheme="red" aria-label="Delete" />
                </HStack>
              </Flex>
            ))
          )}
        </Stack>
      </Box>
    </Container>
  );
}

export default App;
