import axios from 'axios';

const API_URL = 'http://localhost:8080/api/reminders';


export const getReminders = () => axios.get(API_URL);
export const addReminder = (data) => axios.post(API_URL, data);
export const deleteReminder = (id) => axios.delete(`${API_URL}/${id}`);
export const updateReminder = (id, updatedData) =>
  axios.put(`${API_BASE}/reminders/${id}`, updatedData);
