import axios from 'axios';

const API_BASE = 'http://localhost:8080/api/reminders'; // ✅ Replace with your actual backend port if different

export const getReminders = () => axios.get(`${API_BASE}/reminders`);
export const addReminder = (data) => axios.post(`${API_BASE}/reminders`, data);
export const deleteReminder = (id) => axios.delete(`${API_BASE}/reminders/${id}`);
export const updateReminder = (id, data) => axios.put(`${API_BASE}/reminders/${id}`, data);
